package advanced_excel.actions;

import java.io.File;
import java.io.FileInputStream;
import com.mendix.core.Core;
import com.mendix.logging.ILogNode;
import advanced_excel.proxies.DocumentType;
import advanced_excel.Utils;
import org.apache.poi.ss.usermodel.Workbook;
import org.apache.poi.xssf.usermodel.XSSFWorkbook;
import org.apache.poi.hssf.usermodel.HSSFWorkbook;
import com.mendix.systemwideinterfaces.core.IContext;
import com.mendix.webui.CustomJavaAction;
import com.mendix.systemwideinterfaces.core.IMendixObject;

/**
 * Opens an existing workbook for editing
 */
public class Workbook_Open extends CustomJavaAction<java.lang.Boolean>
{
	private java.lang.String FilePath;

	public Workbook_Open(IContext context, java.lang.String FilePath)
	{
		super(context);
		this.FilePath = FilePath;
	}

	@java.lang.Override
	public java.lang.Boolean executeAction() throws Exception
	{
		// BEGIN USER CODE
		try
		{
			String Ext = FilePath.substring(FilePath.lastIndexOf("."));
			if (Ext == "xls") {
				Utils.SetDocumentType(DocumentType.XLS);
			} else {
				Utils.SetDocumentType(DocumentType.XLSX);
			}
			
			Workbook workbook;
			DocumentType docType = Utils.GetDocumentType();
			try (FileInputStream inputStream = new FileInputStream(new File(this.FilePath))) {
				if (docType == DocumentType.XLS) {
					workbook = new HSSFWorkbook(inputStream);
				} else {
					workbook = new XSSFWorkbook(inputStream);
				}
			}
			Utils.SetWorkBook(workbook);
			
			return true;
		} catch (Exception e) {
			logger.error("ERROR in Advanced_Excel.Workbook_Open: " + e.getMessage() + "\n" + e.toString(), e);
			return false;
		} 
		// END USER CODE
	}

	/**
	 * Returns a string representation of this action
	 */
	@java.lang.Override
	public java.lang.String toString()
	{
		return "Workbook_Open";
	}

	// BEGIN EXTRA CODE
	protected static ILogNode logger = Core.getLogger("Advanced_Excel");
	// END EXTRA CODE
}
